import React, {useState} from 'react';

const AccountNavBar = () => {
    const [showData, setShowData] = useState<boolean>(false);
    const [showOrder, setShowOrder] = useState<boolean>(false);
    const [showHistoryOrder, setShowHistoryOrder] = useState<boolean>(false);


    return (
        <form style={{width: 170, height: 550, border: "1px solid lightslategrey", position: "absolute", top: 55}}>
            <div style={{border: "1px solid lightslategrey", marginBottom: 5, margin: -1, display: "flex", justifyContent: "center" }}>
                <button style={{width: "100%"}} onClick={() => setShowData(!showData)}>
                    Особисті дані
                </button>
            </div>
            <div style={{border: "1px solid lightslategrey", marginBottom: 5, margin: -1, display: "flex", justifyContent: "center" }}>
                <button style={{width: "100%"}} onClick={() => setShowOrder(!showOrder)}>
                    Поточне замовлення
                </button>
            </div>
            <div style={{border: "1px solid lightslategrey", marginBottom: 5, margin: -1, display: "flex", justifyContent: "center" }}>
                <button style={{width: "100%"}} onClick={() => setShowHistoryOrder(!showHistoryOrder)}>
                    Історія замовлень
                </button>
            </div>
        </form>
    );
};

export default AccountNavBar;
