import React, {FC, useState} from 'react';
import {Container, Form, Row} from "react-bootstrap";
import {useDispatch} from "react-redux";
import {useTypedSelector} from "../../../hooks/useTypedSelector";
import Delivery from "./Delivery";
import {AccountActionType} from "../../../types/typeAccount";


interface Data {
    phone?: string,
    surname?: string,
    firstName?: string,
    middleName?: string,
    username?: string,
    password?: string,
    passwordEnter?: string,
    deliveryMethod?: string
    addressNovaPoshta?:{
        postOffice: string,
        city: string,
        street: string,
        number: string,
    }
    addressUkrPoshta?: {
        region: string,
        district: string,
        city: string,
        street: string,
        number: string,
        zipcode: string,
    }
}

const RegistrationForm: FC = (): JSX.Element => {
    const dispatch = useDispatch();
    const clientData = useTypedSelector(state => state.account.dataList)
    const [DataList, setDataList] = useState<Data>({
        phone: "",
        surname: "",
        firstName: "",
        middleName: "",
        username: "",
        password: "",
        passwordEnter: "",
        deliveryMethod: "",
        addressNovaPoshta: {
            postOffice: "",
            city: "",
            street: "",
            number: ""
        },
        addressUkrPoshta: {
            region: "",
            district: "",
            city: "",
            street: "",
            number: "",
            zipcode: "",
        },
    })


    const addClientData = (data: Data) => {
        if (data.phone !== undefined && data.phone.length === 13){
            setDataList({...DataList, phone: data.phone});
        }
        if (data.password !== undefined && data.password.length !== 0){
            setDataList({...DataList, password: data.password});
        }
        if (data.passwordEnter !== undefined && data.passwordEnter.length !== 0){
            setDataList({...DataList, passwordEnter: data.passwordEnter});
        }
        if (data.surname !== undefined && data.surname.length !== 0){
            setDataList({...DataList, surname: data.surname});
        }
        if (data.firstName !== undefined && data.firstName.length !== 0){
            setDataList({...DataList, firstName: data.firstName});
        }
        if (data.middleName !== undefined && data.middleName.length !== 0){
            setDataList({...DataList, middleName: data.middleName});
        }
        if (data.username !== undefined && data.username.length !== 0){
            setDataList({...DataList, username: data.username});
        }
        if (data.deliveryMethod !== "") {
            setDataList({...DataList, deliveryMethod: data.deliveryMethod});
        }
        if (data.addressUkrPoshta !== undefined) {
            setDataList({...DataList, addressUkrPoshta: data.addressUkrPoshta});
        }
        if (data.addressNovaPoshta !== undefined) {
            setDataList({...DataList, addressNovaPoshta: data.addressNovaPoshta});
        }


        dispatch({
            type: AccountActionType.ADD_DATA_LIST,
            payload:{
                phone: DataList.phone,
                surname: DataList.surname,
                firstName: DataList.firstName,
                middleName: DataList.middleName,
                username: DataList.username,
                password: DataList.password,
                deliveryMethod: DataList.deliveryMethod,
                addressUkrPoshta: DataList.addressUkrPoshta,
                addressNovaPoshta: DataList.addressNovaPoshta
            }
        })
    }

    return (
        <>
            <Container>
                <Row style={{justifyContent: "center"}}>
                    <Form style={{height: 280}}>
                        <div style={{display: "flex", justifyContent: "right", marginBottom: 10}}>
                            Тел.: {  (DataList.phone !== undefined && DataList.phone.length === 13) || clientData.phone !== ""
                                ? <span style={{color: "green"}}> *</span>
                                : <span style={{color: "red"}}> *</span>
                            }
                            <input
                                style={{width: 140, marginLeft: 5}}
                                type={"tel"}
                                maxLength={13}
                                defaultValue={clientData.phone !== "" ? clientData.phone : "+380"}
                                onChange={(event) => addClientData({phone: event.target.value})}
                            />
                        </div>

                        <div style={{display: "flex", justifyContent: "right", marginBottom: 10}}>
                            Прізвище:  {(DataList.surname !== undefined && DataList.surname.length === 0) || clientData.surname !== ""
                                ? <span style={{color: "green"}}> *</span>
                                : <span style={{color: "red"}}> *</span>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"text"}
                                defaultValue={clientData.surname !== "" ? clientData.surname : ""}
                                onChange={(event) => addClientData({surname: event.target.value})}
                            />
                        </div>

                        <div style={{display: "flex", justifyContent: "right", marginBottom: 10}}>
                            Ім'я:  {(DataList.firstName !== undefined && DataList.firstName.length === 0) || clientData.firstName !== ""
                                ? <span style={{color: "green"}}> *</span>
                                : <span style={{color: "red"}}> *</span>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"text"}
                                defaultValue={clientData.firstName !== "" ? clientData.firstName : ""}
                                onChange={(event) => addClientData({firstName: event.target.value})}
                            />
                        </div>

                        <div style={{display: "flex", justifyContent: "right", marginBottom: 30}}>
                            По-батькові: {(DataList.middleName !== undefined && DataList.middleName.length === 0) || clientData.middleName !== ""
                                ? <span style={{color: "green"}}> *</span>
                                : <span style={{color: "red"}}> *</span>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"text"}
                                defaultValue={clientData.middleName !== "" ? clientData.middleName : ""}
                                onChange={(event) => addClientData({middleName: event.target.value})}
                            />
                        </div>

                        <div style={{display: "flex", justifyContent: "right", marginBottom: 30}}>
                            Псевдонім у форумі: {(DataList.username !== undefined && DataList.username.length === 0) || clientData.username !== ""
                                ? <span style={{color: "green"}}> *</span>
                                : <span style={{color: "red"}}> *</span>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"text"}
                                defaultValue={clientData.username !== "" ? clientData.username : ""}
                                onChange={(event) => addClientData({username: event.target.value})}
                            />
                        </div>
                        <div style={{display: "flex", justifyContent: "right", marginBottom: 30}}>
                            Пароль: {DataList.password === DataList.passwordEnter
                                ? <span style={{color: "green"}}> *</span>
                                : <>{DataList.password === "" ? <span style={{color: "red"}}> *</span> : <span style={{color: "blue"}}> *</span>}</>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"password"}
                                defaultValue={DataList.password !== "" ? DataList.password : ""}
                                onChange={(event) => addClientData({password: event.target.value})}
                            />
                        </div>
                        <div style={{display: "flex", justifyContent: "right", marginBottom: 30}}>
                            Підтвердіть пароль: {DataList.password === DataList.passwordEnter
                                ? <span style={{color: "green"}}> *</span>
                                : <>{DataList.passwordEnter === "" ? <span style={{color: "red"}}> *</span> : <span style={{color: "blue"}}> *</span>}</>
                            }
                            <input
                                style={{marginLeft: 5, width: 350}}
                                type={"password"}
                                defaultValue={""}
                                onChange={(event) => addClientData({passwordEnter: event.target.value})}
                            />
                        </div>
                    </Form>
                </Row>
            </Container>
            <Container style={{marginTop: 20}}>
                <Delivery
                    DeliveryMethod={(str) => addClientData({deliveryMethod: str})}
                    DataNovaPoshta={(obj) => addClientData({addressNovaPoshta: obj})}
                    DataUkrPoshta={(obj) => addClientData({addressUkrPoshta: obj})}
                />
            </Container>

        </>
    );
};

export default RegistrationForm;
