import React, {FC, useEffect, useState} from 'react';
import {useTypedSelector} from "../hooks/useTypedSelector";
import AccountList from "../components/account/AccountList";
import ModuleLogin from "../components/UI/moduleLogin";
import {useDispatch} from "react-redux";
import {fetchAPI} from "../store/action-creators/API_Account";
import {AccountActionType} from "../types/typeAccount";


const Account: FC  = () => {
    const dataList = useTypedSelector(state => state.account.dataList);
    const arrayAPI = useTypedSelector(state => state.API.accountArray)
    const [firstLoad, setFirstLoad] = useState(false);
    const [isLogin, setIsLogin] = useState<boolean>(false);
    const login = useTypedSelector(state => state.account.login)
    const dispatch = useDispatch();


    useEffect(() => {
        if(!firstLoad) {
            // @ts-ignore
            dispatch(fetchAPI())
            setFirstLoad(true);
        }
    }, [firstLoad])

    useEffect(() => {

        if (dataList.phone === ""){console.log("1111")
            for (let i = 0; i < arrayAPI.length; i++) {
                if (arrayAPI[i].phone === login.phone) {
                    dispatch({
                        type: AccountActionType.ADD_DATA_LIST,
                        payload: {
                            phone: arrayAPI[i].phone,
                            name: arrayAPI[i].name,
                            username: arrayAPI[i].username,
                            street: arrayAPI[i].address.street,
                            suite: arrayAPI[i].address.suite,
                            city: arrayAPI[i].address.city,
                            password: arrayAPI[i].address.zipcode,
                        }
                    })

                }
            }
        }

        if((dataList.phone !== "")){
            if((login.password === dataList.password)){
                if(login.phone === dataList.phone)
                setIsLogin(true);
            }
        }

    }, [login.password, login.phone])

    console.log(arrayAPI)
    console.log(login.password.length)
    console.log(login.phone.length)
    console.log(dataList)
    console.log(dataList.phone)
    console.log(dataList.password)
    console.log(login.password === dataList.password)
    console.log(login.phone === dataList.phone)

    return (
        <>
            <h1 style={{margin: 10, marginLeft: 190}}>Особистий кабінет</h1>
            {isLogin
                ?   <AccountList/>

                :   <ModuleLogin/>

            }
        </>
    );
};

export default Account;
