import {FC, useEffect, useState} from "react";
import {Button} from "react-bootstrap";


interface ButtonWithEffects {
    name: string;
    startEffect: boolean;
    onClick:() => void;
    timer_sec: number;
    frequency_1_per_sec?: number | undefined;
    variantFirst: string;
    variantSecond: string;
    colorChange?: boolean | undefined;
    flashing?: boolean | undefined;

}

export const ButtonWithEffects: FC<ButtonWithEffects> = ({
                                                             name,
                                                             variantFirst,
                                                             variantSecond,
                                                             frequency_1_per_sec,
                                                             colorChange,
                                                             flashing,
                                                             startEffect,
                                                             timer_sec,
                                                             onClick
}) => {

    const [effect, setEffect] = useState<boolean>(startEffect);
    const timeEffect: number = Math.abs(timer_sec) * 1000;

    const [counter, setCounter] = useState<number>(Math.abs(timer_sec));


    useEffect(() => {
        let frequency: number = 0
        if((frequency_1_per_sec !== 0) &&( frequency_1_per_sec !== undefined)){
            frequency = 500 / Math.abs(frequency_1_per_sec);
        }

        if (colorChange === true && (effect && timeEffect > 0)) {
            setTimeout(() => {
                setEffect(false);
            }, timeEffect);
        }

        if (flashing === true && (effect && timeEffect > 0)) {
            while (counter > 0) {
                setTimeout(() => {
                    setEffect(false);
                }, frequency);
                setTimeout(() => {
                    setEffect(true);
                }, frequency);
                setCounter(counter - 1);
            }
        }

    },[effect, timeEffect, frequency_1_per_sec])

    return(
      <Button
          onClick={onClick}
          variant={effect ? variantFirst : variantSecond}
      >
          {name}
      </Button>
    );
}
