import React, {FC, useEffect, useState} from 'react';
import {Button, Modal, ModalBody, ModalHeader, ModalTitle} from "react-bootstrap";

import {AccountActionType} from "../../types/typeAccount";
import {useDispatch} from "react-redux";


interface InputValue {
    tel?: string;
    pass?: string;
}

const ModuleLogin: FC = () => {
    const [isLogin, setIsLogin] = useState<boolean>(false);
    const [telState, setTelState] = useState<string>("");
    const [passState, setPassState] = useState<string>("");
    const dispatch = useDispatch();

    const input = ({tel, pass}: InputValue) => {
        if(tel !== undefined)setTelState(tel);
        if(pass !== undefined) setPassState(pass);
    }

    useEffect(() => {
        if((telState?.length === 13) && (passState?.length >= 8)) {
            if (isLogin) {
                dispatch({type: AccountActionType.ENTER_LOGIN, payload: {phone: telState, password: telState}});
            } else setIsLogin(false);
        }
    }, [telState, passState, isLogin])

    return (
        <>
            <Modal show={true}>
                <ModalHeader><ModalTitle>Вхід</ModalTitle></ModalHeader>
                <ModalBody>
                    <input
                        onChange={(event) => input({tel: event.target.value})}
                    />
                    <input
                        onChange={(event) => input({pass: event.target.value})}
                    />
                    <Button variant={"success"} onClick={() => setIsLogin(!isLogin)}>Увійти</Button>
                </ModalBody>
            </Modal>
        </>
    );
};

export default ModuleLogin;
