import React, {FC, useEffect, useState} from 'react';
import {Button, CloseButton, Modal, ModalBody, ModalHeader, ModalTitle} from "react-bootstrap";

export interface PropsModuleLogin {
    setPhone: (tel: string | undefined) => void,
    setPassword: (pass: string | undefined) => void,
    isShow: boolean;
}

interface InputValue {
    tel?: string;
    pass?: string;
}

const ModuleLogin: FC<PropsModuleLogin> = (props: PropsModuleLogin) => {
    const [login, setLogin] = useState<boolean>(false);
    const [telState, setTelState] = useState<string>("");
    const [passState, setPassState] = useState<string>("");
    const [show, setShow] = useState(false);
    const input = ({tel, pass}: InputValue) => {
        if(tel !== undefined)setTelState(tel);
        if(pass !== undefined) setPassState(pass);
    }

    useEffect(() => {
        if(isShow) setShow(true);
        if((telState?.length === 13) && (passState?.length >= 8)) {
            if (login) {
                props.setPhone(telState);
                props.setPassword(passState);
                setShow(false);
            } else setLogin(false);
        }
    }, [telState, passState, login, isShow])

    return (
        <>
            <Modal show={show}>
                <ModalHeader><ModalTitle>Вхід</ModalTitle><CloseButton onClick={() => setShow(false)}/></ModalHeader>
                <ModalBody>
                    <input
                        onChange={(event) => input({tel: event.target.value})}
                    />
                    <input
                        onChange={(event) => input({pass: event.target.value})}
                    />
                    <Button variant={"success"} onClick={() => setLogin(!login)}>Увійти</Button>
                </ModalBody>
            </Modal>
        </>
    );
};

export default ModuleLogin;
