import {
    AccountAction,
    AccountActionType,
    AccountState,
} from "../../types/typeAccount";



const initialState: AccountState = {
    dataList: {
        phone: "",
        surname: "",
        firstName: "",
        middleName: "",
        username: "",
        password: "",
        deliveryMethod: "SelfDelivery" || "NovaPoshta" || "UkrPoshta",
        address: "" || {postOffice: "", city: "", street: "", number: ""}
                    || {region: "", district: "", city: "", street: "", number: "", zipcode: "",}
    },

    addressNovaPoshta: {
        postOffice: "",
        city: "",
        street: "",
        number: ""
    },

    addressUkrPoshta: {
        region: "",
        district: "",
        city: "",
        street: "",
        number: "",
        zipcode: "",
    },

    historyOrder: [],

    login: {
        phone: "",
        password: "",
        login: false,
    },
}

export const accountReducer = (state = initialState,  action: AccountAction): AccountState => {
    switch (action.type){
        case AccountActionType.ADD_DATA_LIST_NOVA_POSHTA:
            return {...state, addressNovaPoshta: action.payload}

        case AccountActionType.ADD_DATA_LIST_UKR_POSHTA:
            return {...state, addressUkrPoshta: action.payload}

        case AccountActionType.ADD_DATA_LIST:
            return {...state, dataList: action.payload}

        case AccountActionType.ADD_HISTORY_ORDER:
            return {...state, historyOrder: action.payload}

        case AccountActionType.ENTER_LOGIN:
            return {...state, login: action.payload}

        default:
            return state;
    }
}
