import React, {useState} from 'react';
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {Col, Container, Row} from "react-bootstrap";

const AccountData: React.FC = () => {
    const [first, setFirst] = useState<boolean>(true)
    const dataList = useTypedSelector(state => state.account.dataList);
    const address = () => {
        if (first)
        if((dataList.address !== undefined)&&(dataList.address !== "")){
            for (let i = 0; i < dataList.address.length; i++){
                if(dataList.address[i] !== "")
                    return (<Col>{dataList.address[i]}</Col>)
            }
            setFirst(false)
        }
    }
    return (
        <>
            <Container>
                <Row>
                    <Col>
                        id-Телефон: {dataList.phone}
                    </Col>
                    <Col>
                        Прізвище: {dataList.surname}
                    </Col>
                    <Col>
                        Ім'я: {dataList.firstName}
                    </Col>
                    <Col>
                        По-батькові: {dataList.middleName}
                    </Col>
                    <Col>
                        Nike у форумі: {dataList.username}
                    </Col>
                </Row>
                <Row>
                    <Row>Спосіб доставки: {dataList.deliveryMethod}</Row>
                    <Row>
                        <Col>
                            {address}
                        </Col>
                    </Row>
                </Row>
            </Container>
        </>
    );
};

export default AccountData;
