import React, {useEffect, useState} from 'react';
import AccountData from "../components/account/accountData";
import ModuleLogin from "../components/account/moduleLogin";
import {useTypeSelector} from "../hooks/useTypeSelector";
import {Button} from "react-bootstrap";

interface Phone{
    phone: string;
}

const Account = () => {
    const dataList = useTypeSelector(state => state.account.dataList);
    const [isLogin, setIsLogin] = useState(false);
    const [password, setPassword] = useState<string | undefined>(undefined);
    const [phone, setPhone] = useState<string | undefined>(undefined);
    const [show, setShow] = useState<boolean>(false)
    useEffect(() => {
        if((password !== undefined)&&(phone !== undefined)){
            setIsLogin(true);
        }else setIsLogin(false);
    }, [password, phone])

    return (
        <>
            <h1>Особистий кабінет</h1>
            {isLogin
                ?   <AccountData/>

                :   <>
                        <Button onClick={() => setShow(true)}>Увійти в особистий кабінет</Button>
                        <ModuleLogin setPhone={setPhone} setPassword={setPassword} isShow={show}/>
                    </>
            }
        </>
    );
};

export default Account;
