import React, {FC, useEffect, useState} from 'react';
import {Button, Modal, ModalBody, ModalHeader, ModalTitle} from "react-bootstrap";

import {AccountActionType} from "../../types/typeAccount";
import {useDispatch} from "react-redux";
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {fetchAPI} from "../../store/action-creators/API_Account";
import {Simulate} from "react-dom/test-utils";
import click = Simulate.click;
import {GetAccountFromAPI, useGetAccountFromAPI} from "../account/GetAccountFromAPI";


interface InputValue {
    tel?: string;
    pass?: string;
}

const ModuleLogin: FC = () => {
    const dispatch = useDispatch();
    const dataList = useTypedSelector(state => state.account.dataList);
    const [firstLoad, setFirstLoad] = useState<boolean>(false);
    const [isLogin, setIsLogin] = useState<boolean>(false);
    const [phone, setPhone] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const [clickLogin, setClickLogin] = useState<boolean>(false);

    const input = ({tel, pass}: InputValue) => {
        if(tel !== undefined)setPhone(tel);
        if(pass !== undefined) setPassword(pass);
    }

    useEffect(() => {
        if(!firstLoad) {
            // @ts-ignore
            dispatch(fetchAPI())
            setFirstLoad(true);
        }
    },[])

    useEffect(() => {
        if (phone !== "") GetAccountFromAPI(phone);

        if(clickLogin){
            if((password === dataList.password)){
                if(phone === dataList.phone){
                    setIsLogin(true);
                    dispatch({type: AccountActionType.ENTER_LOGIN, payload: {login: isLogin}});
                }
            }
        }
    }, [password, phone, clickLogin, firstLoad])


    return (
        <>
            <Modal show={true}>
                <ModalHeader><ModalTitle>Вхід</ModalTitle></ModalHeader>
                <ModalBody>
                    <input
                        onChange={(event) => input({tel: event.target.value})}
                    />
                    <input
                        onChange={(event) => input({pass: event.target.value})}
                    />
                    <Button variant={"success"} onClick={() => setClickLogin(!clickLogin)}>Увійти</Button>
                </ModalBody>
            </Modal>
        </>
    );
};

export default ModuleLogin;
