import React, {FC, useEffect, useState} from 'react';
import {Row, Col, Form, Card, Button, Container} from "react-bootstrap";
import {ButtonFacebook, ButtonSkype, ButtonsTwitter, ButtonTelegram} from "../UI/ButtonsSocialNetworks";
import {useDispatch} from "react-redux";
import {useTypedSelector} from "../../hooks/useTypedSelector";
import {AccountActionType} from "../../types/typeAccount";
import ModuleLogin from "../UI/ModuleLogin";


interface PlaceOrderProductForm {
    IsOrderAccepted: () => void,
    ShowModuleOrder: () => void,
}

interface dataProps {
    telNumber?: string,
    firstName?: string,
    surname?: string,
    middleName?: string,
    methodCommunication?: string,
    address?: string,
}

const PlaceOrderProductForm: FC<PlaceOrderProductForm> = ({IsOrderAccepted, ShowModuleOrder}): JSX.Element => {
    const textEvent = {text: "Event-text"};
    const [isEventText, setIsEventText] = useState<boolean>(false);
    const [isVisible, setIsVisible] = useState<boolean>(false);
    const isLogin = useTypedSelector(state => state.account.login.login);
    const clientData = useTypedSelector(state => state.account.dataList);
    const productList = useTypedSelector(state => state.product.product);
    const totalCost = useTypedSelector(state => state.product.totalCost)
    const [clickLogin, setClickLogin] = useState<boolean>(false);
    const [DataList, setDataList] = useState({
                        telNumber: "",
                        firstName: "",
                        surname: "",
                        middleName: "",
                        methodCommunication: "",
                        address: "",
    })
    const dispatch = useDispatch();
    const visibleForm = () => setIsVisible(!isVisible);
    const addClientData = (data: dataProps) => {
        if (data.telNumber !== undefined && data.telNumber.length === 13){
            setDataList({...DataList, telNumber: data.telNumber});
        }

        if (data.surname !== undefined && data.surname.length !==0){
            setDataList({...DataList, surname: data.surname});
        }

        if (data.firstName !== undefined && data.firstName.length !== 0){
            setDataList({...DataList, firstName: data.firstName});
        }

        if (data.middleName !== undefined && data.middleName.length !==0){
            setDataList({...DataList, middleName: data.middleName});
        }

        if (data.address !== undefined && data.address.length !==0){
            setDataList({...DataList, address: data.address});
        }
